mapper.select:
SELECT count(*) FROM usertb
WHERE uemail = #{uemail }
AND upassword = #{upassword }

mapper.update:
 	UPDATE usertb
 	SET uphone = #{uphone },
		uaddress1 = #{uaddress1 },
		uaddress2 = #{uaddress2 },
		uaddress3 = #{uaddress3 }
	WHERE uemail = #{uemail }

mapper.select:
SELECT * FROM (
SELECT rownum rnum, Q.* FROM (
SELECT question.*
FROM question
WHERE 1=1
ORDER BY questionno DESC
) Q
ORDER BY rnum
) QUESTION
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM usertb 
WHERE 1=1
AND userno =#{userno }
