mapper.select:
SELECT * FROM (
SELECT rownum rnum, B.* FROM (
SELECT volunteer.*
FROM volunteer
WHERE 1=1

	AND vol_process = #{v_process}


	AND department = #{v_department}


	AND vol_area = #{v_area}


	
	AND (vol_sterm <= #{v_sterm} AND vol_sterm <= #{v_eterm} and
	vol_eterm>= #{v_sterm} and vol_eterm>= #{v_eterm}
	OR vol_sterm BETWEEN #{v_sterm} AND #{v_eterm}
	OR vol_eterm BETWEEN #{v_sterm} AND #{v_eterm})
	


	
	AND (stime <= #{stime} AND stime <= #{etime} and
	etime>= #{stime} and etime>= #{etime}
	OR stime BETWEEN #{stime} AND #{etime}
	OR etime BETWEEN #{stime} AND #{etime})
	


	AND vol_title LIKE '%' || #{search} || '%'

ORDER BY volunteerno DESC
) B
ORDER BY rnum
) VOLUNTEER
WHERE rnum BETWEEN
#{startNo} AND #{endNo}

SELECT COUNT(*) FROM volunteer
WHERE 1=1

	AND vol_process = #{v_process}


	AND department = #{v_department}


	AND vol_area = #{v_area}


	
	AND (vol_sterm <= #{v_sterm} AND vol_sterm <= #{v_eterm} and
	vol_eterm>= #{v_sterm} and vol_eterm>= #{v_eterm}
	OR vol_sterm BETWEEN #{v_sterm} AND #{v_eterm}
	OR vol_eterm BETWEEN #{v_sterm} AND #{v_eterm})
	


	
	AND (stime <= #{stime} AND stime <= #{etime} and
	etime>= #{stime} and etime>= #{etime}
	OR stime BETWEEN #{stime} AND #{etime}
	OR etime BETWEEN #{stime} AND #{etime})
	


	AND vol_title LIKE '%' || #{search} || '%'
